<div class="language-python line-numbers-mode" data-ext="py"><pre v-pre class="language-python"><code><span class="token keyword">import</span> re
<span class="token keyword">import</span> requests
num<span class="token operator">=</span><span class="token number">1</span>
url<span class="token operator">=</span><span class="token string">"https://www.shijuan1.com/a/sjyy6/list_609_"</span><span class="token operator">+</span><span class="token builtin">str</span><span class="token punctuation">(</span>num<span class="token punctuation">)</span><span class="token operator">+</span><span class="token string">".html"</span>
resp<span class="token operator">=</span>requests<span class="token punctuation">.</span>get<span class="token punctuation">(</span>url<span class="token punctuation">)</span>
resp<span class="token punctuation">.</span>encoding<span class="token operator">=</span>resp<span class="token punctuation">.</span>apparent_encoding

obj<span class="token operator">=</span>re<span class="token punctuation">.</span><span class="token builtin">compile</span><span class="token punctuation">(</span><span class="token string">r"&lt;td width='52%' height='23'>&lt;a href=\"(?P&lt;href>.*?)"</span>
               <span class="token string">r"\" class=\"title\" target='_blank'>"</span><span class="token punctuation">,</span>re<span class="token punctuation">.</span>S<span class="token punctuation">)</span>
